
const is_high_surrogate  =(n)=>((n>>8) & 0b1111_1100) === 0b1101_1000;
const is_low_surrogate   =(n)=>((n>>8) & 0b1111_1100) === 0b1101_1100;
const is_not_surrogate   =(n)=>(!is_high_surrogate(n)) && (!is_low_surrogate(n));


const surrogate_to_code     = (n16_0,n16_1)=> (
     ((n16_0 & 0x03ff) << 10) +
     ((n16_1 & 0x03ff)) +
     0x10000
)


const b16arr_to_code = (b16arr,lngth)=> {
    if(lngth===1) {
        return(b16arr[0])
    }  else {
        return(surrogate_to_code(b16arr[0],b16arr[1]))
    }
}


module.exports = {
   is_high_surrogate,
   is_low_surrogate,
   is_not_surrogate,
   surrogate_to_code, 
   b16arr_to_code 
}
