const assert = require("assert");

const v8 = require("v8");
const {ReadBigInt} = require("../r");

console.log("10000n+2n**64n :" ,10000n+2n**64n)

var buf = v8.serialize(-(10000n+2n**64n));

console.log(buf);

console.log(ReadBigInt(buf,3,{}));

assert(v8.deserialize(buf)=== ReadBigInt(buf,3,{})[0])

var buf = v8.serialize((10000n+2n**64n));
console.log(ReadBigInt(buf,3,{}));


assert(v8.deserialize(buf)=== ReadBigInt(buf,3,{})[0])
