const _A = Array;
  
const MAX_NID = 2**53-1;

class Nid extends Array {
    constructor() {super();this[0]=0;}
    get id() {return(this[0])}
    next() {
        let id  = this[0]
        let nid = this[0]+1;
        if(nid-1 === MAX_NID) {nid = 0}
        this[0] = nid;
        return(id)
    }
}

const creat_nid = ()       =>new Nid();

module.exports = {
   MAX_NID,creat_nid,
}
