const {istof}      = require("nv-facutil-typis");

const _ERR         = Error;
const creat_err    = (s) => new _ERR(s);
const thrw_str     = (s) => {throw(creat_err(s))}
const is_err       = (o) => istof(o,_ERR);
const unreachable  = (s) => thrw_str("unreachable");

module.exports = {
    _ERR,
    creat_err,
    thrw_str,
    is_err,
    unreachable,
}
