const _u = undefined;
const _n = null;
const _t = true;
const _f = false;

const is_un    = (o) => o===_u;
const is_nu    = (o) => o===_n;
const is_unnu  = (o) => o===_u || o===_n;

const is_tru   = (o) => o===_t;  
const is_fls   = (o) => o===_f;
const is_bl    = (o) => o===_t || o===_f;

const is_untf  = (o) => is_unnu(o) || is_bl(o);

const nuize    = (o) => is_unnu(o)?_n:o;
const unize    = (o) => is_unnu(o)?_u:o; 


